cat cart.alignment.txt | grep -v hap | perl -ne 's/U133A://;s/;//;chomp;@F=split/\t/;$midpoint=int($F[16]+(($F[17]-$F[16])/2));print join("\t",$F[14],$midpoint-3000000,$midpoint+3000000,$F[10]),"\n";' > cart.region.txt
cat cart.region.txt | grep chr | perl -ne 'chomp;@F=split/\t/;print "echo $F[3]; wget -O - -q '\''http://genome.ucsc.edu/cgi-bin/hgTables?clade=vertebrate&org=Human&db=hg18&hgta_group=regulation&hgta_track=affyU133Plus2&hgta_table=affyU133Plus2&hgta_regionType=range&position=$F[0]:$F[1]-$F[2]&hgta_outputType=primaryTable&boolshad.sendToGalaxy=1&hgta_outFileName=&hgta_compressType=none&hgta_doTopSubmit=get+outputls'\'' | perl -ne '\''chomp;\@F=split/\\t/;\$midpoint=int(\$F[16]+((\$F[17]-\$F[16])/2));print join(\"\\t\",\$F[14],\$midpoint,\$midpoint,\$F[10]),\"\\n\";'\'' > region/$F[3].dat\n"' | bash
